import { useEffect, useMemo, useState } from 'react';
import { useTheme } from 'styled-components';
import Image from 'next/image';

import { Button, Header } from 'components';
import { Main, AboutSection, TechsSection, TechItem } from 'styles/pages/home';
import { sizes } from 'styles/sizes';

export default function Home() {
  const {
    images: { profilePic, techsSVG },
    colors: { primary, office },
  } = useTheme();
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < sizes.desktop_sm);
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const techsImages = useMemo(() => {
    const techs = [
      {
        name: 'React',
        image:
          'data:image/svg+xml;base64,',
        url: 'https://reactjs.org/',
      },
      {
        name: 'React 1',
        image:
          'data:image/svg+xml;base64,',
        url: 'https://reactjs.org/',
      },
      {
        name: 'React 2',
        image:
          'data:image/svg+xml;base64,',
        url: 'https://reactjs.org/',
      },
      {
        name: 'React 3',
        image:
          'data:image/svg+xml;base64,',
        url: 'https://reactjs.org/',
      },
      {
        name: 'React 4',
        image:
          'data:image/svg+xml;base64,',
        url: 'https://reactjs.org/',
      },
      {
        name: 'React 5',
        image:
          'data:image/svg+xml;base64,',
        url: 'https://reactjs.org/',
      },
    ];

    return techs.map((tech) => (
      <li key={tech.name}>
        <TechItem href={tech.url} target="_blank" rel="external noreferrer">
          <Image src={tech.image} alt={tech.name} width={80} height={80} />
          {tech.name}
        </TechItem>
      </li>
    ));
  }, []);

  return (
    <>
      <Header />
      <Main>
        <AboutSection>
          <Image src={profilePic} alt="profile" priority />
          <span>
            <h1>
              Hello There. <br />
              {isMobile ? "I'm Rafael," : 'I am Rafael Domingues,'}
              <br />
              <span>Front End Developer</span>
            </h1>
            <small>and Jedi Master</small>
            <p>
              Currently based in Rio de Janeiro, Brazil, Planet Earth. Working
              with the development and improvement of a Design System and, in
              the meantime, helping the galaxy find peace.
            </p>

            <Button color={office} backgroundColor={primary}>
              Get in touch ðŸ¤˜
            </Button>
          </span>
        </AboutSection>

        <TechsSection>
          <h1>
            Techs <Image src={techsSVG} alt="" />
          </h1>

          <ul>{techsImages}</ul>
        </TechsSection>
      </Main>
    </>
  );
}
